using System; using EUJIT.CustomControl; using EUJIT.iOS.Renderer; using UIKit; using Xamarin.Forms; using Xamarin.Forms.Platform.iOS;  [assembly: ExportRenderer(typeof(ExtendedViewCell), typeof(ExtendedViewCellRenderer))] namespace EUJIT.iOS.Renderer {     public class ExtendedViewCellRenderer : ViewCellRenderer     {         public ExtendedViewCellRenderer()         {         }          public override UITableViewCell GetCell(Cell item, UITableViewCell reusableCell, UITableView tv)         {             var cell = base.GetCell(item, reusableCell, tv);             var view = item as ExtendedViewCell;             cell.SelectedBackgroundView = new UIView             {                 BackgroundColor = view.SelectedBackgroundColor.ToUIColor(),             };              return cell;         }     } } 